mapper.insert:
insert into banner (bid, adName, type, status, photo, textOfAd, linkOfAd
  )
values (#{bid}, #{adName}, #{type}, #{status}, #{photo}, #{textOfAd}, #{linkOfAd}
  )

mapper.select:
select bid, adName, type, status, photo, textOfAd, linkOfAd
from banner
where bid = #{bid}

select bid, adName, type, status, photo, textOfAd, linkOfAd
from banner
limit #{start}, #{pageSize}
