mapper.delete:
delete from eostoken
where UID = #{UID}
  and type = #{type}

mapper.update:
update eostoken
set EOSAccountName = #{EOSAccountName},
  lockedAmount = #{lockedAmount},
  availableAmount = #{availableAmount},
  amount = #{amount}
where UID = #{UID}
  and type = #{type}

mapper.select:
select UID, type, EOSAccountName, lockedAmount, availableAmount, amount
from eostoken
where UID = #{UID}
  and type = #{type}
