mapper.insert:
insert into ethtoken (UID, type, ETHAddress, lockedAmount, availableAmount, amount
  )
values (#{UID}, #{type}, #{ETHAddress}, #{lockedAmount}, #{availableAmount}, #{amount}
  )

mapper.update:
update ethtoken
set ETHAddress = #{ETHAddress},
  lockedAmount = #{lockedAmount},
  availableAmount = #{availableAmount},
  amount = #{amount}
where UID = #{UID}
  and type = #{type}

mapper.select:
select UID, type, ETHAddress, lockedAmount, availableAmount, amount
from ethtoken
where UID = #{UID}
  and type = #{type}
