mapper.update:
update menu
set text = #{text},
  url = #{url},
  parent_id = #{parent.mid}
where mid = #{mid}

mapper.select:
select mid, text, url, parent_id
from menu
where mid = #{mid}

select mid, text, url, parent_id
from menu
where parent_id = #{mid}

select m.mid, m.text, m.url, m.parent_id
from menu m join role_menu rm on m.mid = rm.mid
where rm.rid = #{rid}
